<span class="token keyword">pub</span> <span class="token keyword">fn</span> <span class="token function-definition function">is_prime</span><span class="token punctuation">(</span>n<span class="token punctuation">:</span> <span class="token keyword">usize</span><span class="token punctuation">)</span> <span class="token punctuation">-></span> <span class="token keyword">bool</span> <span class="token punctuation">{</span>
