  <span class="token keyword">let</span> sqrt_n <span class="token operator">=</span> <span class="token punctuation">(</span>n <span class="token keyword">as</span> <span class="token keyword">f32</span><span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">sqrt</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token keyword">as</span> <span class="token keyword">usize</span><span class="token punctuation">;</span>
